import React from 'react';
import { BrowserRouter as Router, Route, Link, NavLink } from 'react-router-dom';
import './signup.css';

export default class Signup extends React.Component{
  
    constructor(props){
        super(props);

        this.state={
            RegNo:" ",
            ChasisNo:" ",
            Username:" ",
            Email:" ",
            Password:" ",
            ConfirmPassword:" "
        };
    }
  
  
    handleChange = event => {
        this.setState({
          [event.target.id]: event.target.value
        });
      }
  
    
    
    validateForm() {
        return (
          this.state.Email.length > 0 &&
          this.state.Password.length > 0 &&
          this.state.Password === this.state.ConfirmPassword
        );
      }  

    
    
    
    handleSubmit(e) {
        e.preventDefault();

        console.log('The form was submitted with the following data:');
        console.log(this.state);
    }

  
  
  
  
  
  
  
  
    render(){  
    return(
    <div className="App">
        <div className="App__Aside"></div>

        <div className="App__Form">

            <form className="FormTitle" onSubmit={this.validateForm && this.handleSubmit}>
                <Router>
                
                    <div className="PageSwitcher">
                        <NavLink to="/sign-in" activeClassName="PageSwitcher__Item--Active" className="PageSwitcher__Item">Sign In</NavLink>
                        <NavLink exact to="/" activeClassName="PageSwitcher__Item--Active" className="PageSwitcher__Item">Sign Up</NavLink>
                    </div> 
                    
                </Router>

            
                <h2 >Create Your Account</h2>

                <p><input required placeholder="Registration Number" type="number" className="form-control" value={this.state.RegNo} onChange={this.handleChange}></input></p>


                <p><input required placeholder="Chasis Number" type="number" className="form-control" value={this.state.ChasisNo} onChange={this.handleChange}></input></p>


                <p><input required placeholder="Username" tyep="text" className="form-control" value={this.state.Username} onChange={this.handleChange}></input></p>


                <p><input  required placeholder="Email" type="Email" className="form-control" value={this.state.Email} onChange={this.handleChange}></input></p>
                
                
                <p><input required placeholder="Password" type="Password" className="form-control" value={this.state.Password} onChange={this.handleChange}></input></p>


                <p><input required placeholder="Confirm Password" type="Password" className="form-control" value={this.state.ConfirmPassword} onChange={this.handleChange}></input></p>

                <input type="Submit" value="Create Account" class="btn btn-primary btn-lg btn-block"></input>


            
            </form> 
        
        
        </div>
    </div>
    );
  }
}
